mapper.update:
update user_mst set user_time=#{user_time2}, user_create_date = null WHERE user_id = #{user_id};
update seat_mst set user_id = 0 WHERE user_id = #{user_id};
update reserved_seat_mst set user_id = 0 WHERE user_id = #{user_id};

update user_mst set user_time=null, user_date=null WHERE user_id = #{user_id};
update seat_mst set user_id = 0, seat_start_time= null WHERE user_id = #{user_id};
update reserved_seat_mst set user_id = 0, reserved_start_time=null WHERE user_id = #{user_id};
update receipt_mst set receipt_onoff = 0 WHERE user_id = #{user_id};

mapper.select:
SELECT um.user_id, um.user_create_date, um.user_update_date, um.user_time, um.user_date, um.user_out, rm.receipt_time, rm.receipt_day, rm.receipt_onoff, rm.receipt_start_date, rm.receipt_kinds,
sm.seat_id, sm.seat_start_time,
rsm.reserved_seat_id FROM user_mst um left outer join receipt_mst rm on(rm.user_id = um.user_id) left outer join seat_mst sm on(sm.user_id = um.user_id) left outer join reserved_seat_mst rsm on(rsm.user_id = um.user_id) WHERE rm.receipt_kinds != "locker" and um.user_id = #{user_id};
